namespace Xunet.MiniFormium.Simples.Windows;

/// <summary>
/// 登录窗体
/// </summary>
public class LoginForm(MainForm mainForm) : QRCodeForm
{
    /// <summary>
    /// 窗体标题
    /// </summary>
    protected override string Title => "扫码登录";

    /// <summary>
    /// 加载二维码
    /// </summary>
    /// <returns></returns>
    protected override async Task LoadQRCodeAsync()
    {
        var bytes = Convert.FromBase64String("");

        await AppendQRCodeAsync(bytes);
    }

    /// <summary>
    /// 检查登录
    /// </summary>
    /// <returns></returns>
    protected override async Task<bool> CheckLoginAsync()
    {
        // 模拟扫码登录
        await Task.Delay(3000);

        // 登录成功
        return true;
    }

    /// <summary>
    /// 登录成功
    /// </summary>
    /// <returns></returns>
    protected override async Task LoginSuccessAsync()
    {
        await ShowFormAsync(mainForm);
    }
}
